import React from 'react'
import Button from './button'
import Green from '../media/green.png'
import GreenOn from '../media/green_on.png'
import Red from '../media/red.png'
import RedOn from '../media/red_on.png'
import Yellow from '../media/yellow.png'
import YellowOn from '../media/yellow_on.png'
import Blue from '../media/blue.png'
import BlueOn from '../media/blue_on.png'
import Vline from '../media/v_line.png'
import Hline from '../media/h_line.png'

export default class task2 extends React.Component {

    constructor(props){
        super(props)
        this.Click = this.Click.bind(this)
        this.state = {
            sequence: [],
            btnOn: false,
            green: Green,
            red: Red,
            yellow: Yellow,
            blue: Blue
        }
    }

    render() {
        return(
            <div>
                <div Style=" position: relative; display: inline-block;">
                <Button image={Green} on={GreenOn} onClick={this.Click} btnOn={this.state.btnOn}/>
                </div>
                
                <input type="image" src={Vline}></input>
                <div Style=" position: relative; display: inline-block;">
                <Button image={Red} on={RedOn} onClick={this.Click}/>
                </div>

                <div Style=" position: relative; display: block;">
                <input type="image" src={Hline}></input>
                </div>

                <div Style=" position: relative; display: inline-block;">
                <Button image={Yellow} on={YellowOn} onClick={this.Click}/>
                </div>

                <input type="image" src={Vline}></input>
                <div Style=" position: relative; display: inline-block;">
                <Button image={Blue} on={BlueOn} onClick={this.Click}/>
                </div>
            </div>
        )
    }

    Click() {
        this.setColor()
        console.log('click')
    }

    //Pone el color al boton
    setColor() {
        this.setState({btnOn: true})
        setTimeout(function() {
            this.setState({ btnOn: false })
        }.bind(this), 750);
    }


    getNext() {
        return Math.floor(Math.random() * 4);  
    }
}
