export default class task1 extends React.Component {
    
    render() {
        let numbers = [0, 1, 2]
        this.findNumbers()
        return (
            <div>
            <h2 align="center">Los numeros rectangulares, tal que a + b + c = 100 son :</h2>
            <h2 align="center">a: {numbers[0]}</h2>
            <h2 align="center">b: {numbers[1]}</h2>
            <h2 align="center">c: {numbers[2]}</h2>
        </div>
        )
    }
    
    //Encuentra los numeros solicitados
    findNumbers(){
        let squares = this.getSquares
        let numbers = []
        //console.log(squares)
        for (let i = 0; i < squares.length; i++) {
            const a = squares[i];

            for (let j = 0; j < squares.length; j++) {
                const b = squares[j];
                
                for (let k = 0; k < squares.length; k++) {
                    const c = squares[k];
                    console.log("si")
                    if (this.getSum(a, b, c) && this.getRectangle(a, b, c)) {
                        console.log(a, b, c)
                        numbers.push(a)
                        numbers.push(b)
                        numbers.push(c)
                    }

                }
            }

        }
        console.log(numbers)
        //return numbers;
    }

    //Determina si los numeros suman 1000
    getSum(a, b, c){
        return (a + b + c === 1000) ? true : false;
    }

    //Determina si son numeros rectangulares
    getRectangle(a, b, c) {
        return (a^2 + b^2 === c^2) ? true : false;
    }
    

    //Encuentra todas las raices posibles < sqrt (1000)
    getSquares() {
        let squares = [1,2]
        
        //max square < 1000 = 31
        for (let i = 0; i < 31; i++) {
            //squares.push(i*i)
        }
        squares.push(1)
        console.log(squares)
        return squares
    }

    
}

