import React from 'react'
import Green from '../media/green.png'
import GreenOn from '../media/green_on.png'
import Red from '../media/red.png'
import RedOn from '../media/red_on.png'
import Yellow from '../media/yellow.png'
import YellowOn from '../media/yellow_on.png'
import Blue from '../media/blue.png'
import BlueOn from '../media/blue_on.png'

export default class button extends React.Component {

    constructor(props){
        super(props)

        this.Click = this.Click.bind(this)

        this.state = {
            img: this.props.image
        }
    }

    render() {
        return(
            <div>
                <input type="image" src={this.state.img} onClick={this.Click}></input>
            </div>
        )
    }

    Click() {
        this.setColor()
        
        switch (this.props.image) {
            case Green:
                this.props.act(0)
                break;
            case Yellow:
                this.props.act(2)
                break;
            case Blue:
                this.props.act(3)
                break;
            case Red:
                this.props.act(1)
                break;
            default:
                break;
        }
    }

    setColor() {
        this.setState({ img: this.props.on })
        setTimeout(function() {
            this.setState({ img: this.props.image })
        }.bind(this), 750);
    }

    
}
